mapper.select:
select float_new_id, float_new_title, float_url,date_format(create_date,'%Y-%m-%d %H:%i:%s') as create_date,author from cahg_float_news where float_new_status = '0' and 1 = 1
and (float_new_title like #{title} OR DATE_FORMAT(create_date,'%Y-%m-%d') like #{title})
ORDER BY create_date DESC limit #{page},#{rows}
