mapper.select:
select job_id, title,date_format(create_user_date,'%Y-%m-%d') create_date , date_format(end_time,'%Y-%m-%d') end_time from cahg_job where 1 = 1
and (title like #{title} OR DATE_FORMAT(create_user_date,'%Y%m%d') like #{title})
and status = #{status}
and ${condition}
ORDER BY create_date DESC limit #{page},#{rows}

select count(*) from cahg_job where 1 = 1
and (title like #{title} OR DATE_FORMAT(create_user_date,'%Y%m%d') like #{title})
and status = #{status}
and ${condition}

select r.*,d.name as deptName from cahg_job_result r INNER JOIN sys_dept d on r.dept_id = d.dept_id where 1 = 1
and r.job_id = #{jobId}
ORDER BY r.create_time DESC
