mapper.select:
select meeting_notice_id,title,date_format(create_date,'%Y-%m-%d %H:%i:%s') create_date,author from cahg_meeting_notice where status = '0' and 1 = 1
and (title like #{title} OR DATE_FORMAT(create_date,'%Y%m%d') like #{title})
ORDER BY rank='', rank ASC , create_date DESC limit #{page},#{rows}
